mapper.select:
        select d.doId , d.doName , s.siName 
		from do d left join si s on d.doId = s.doId
		where s.siName is not null;

select b.bId, title, writer,siName, regAt, modAt, hit, category, count(bl.bId) as likeCnt 
from board b left join boardLike bl on b.bId = bl.bId 
where 
siName = #{siName} 

and ${field} like "%${query}%"  


and category like '%${category}%'

group by b.bId

order by regAt ${orderBy}

limit #{start}, #{cnt}

select count(bId) from board where siName = #{siName}
and ${field} like "%${query}%"
and category like '%${category}%'

select b.bId , title, writer, siName, category ,regAt, modAt,hit, COUNT(bl.bid) as likeCnt from board b left join boardLike bl on b.bId = bl.bid where siName = #{siName}
and category like "%${category}%"
GROUP by b.bid order by count(bl.bid) desc limit 5;
